import createStore from "unistore"
import { stat } from "fs";

const initState = {
    full_name: "",
    url_to_image: "",
    is_login: false,
    username: "",
    password: "",
    main_list: [],
    main_loading: true,
    now_time: new Date(),
    search: "",
    is_query: false
};

export const store = createStore(initState);

export const actions = store => ({
    handleChange: (state, event) => {
        return { [event.target.name] : event.target.value };
    },
    handleInput: (state, key, value) => {
        return { key : value };
    }
});
